// Initialize language switching and persist selection
let currentLang = localStorage.getItem('osio-lang') || 'ru';

function applyLang(lang) {
  currentLang = lang;
  localStorage.setItem('osio-lang', lang);
  document.documentElement.setAttribute('lang', lang);
  document.querySelectorAll('.lang-switch button').forEach(btn => {
    btn.classList.toggle('active', btn.dataset.lang === lang);
  });
  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    const value = translations[lang] && translations[lang][key];
    if (value) {
      el.innerHTML = value;
    }
  });
}

document.addEventListener('DOMContentLoaded', () => {
  applyLang(currentLang);
});

document.addEventListener('partialsLoaded', () => {
  document.querySelectorAll('.lang-switch button').forEach(btn => {
    btn.addEventListener('click', () => applyLang(btn.dataset.lang));
  });
  applyLang(currentLang);

  const currentPage = window.location.pathname.split('/').pop() || 'index.html';
  document.querySelectorAll('nav a').forEach(link => {
    if (link.getAttribute('href') === currentPage) {
      link.classList.add('active');
    }
  });
});

// Modal functions
function showModal(id) {
  const modal = document.getElementById('modal-' + id);
  if (modal) {
    modal.style.display = 'flex';
  }
}

function closeModal(id) {
  const modal = document.getElementById('modal-' + id);
  if (modal) {
    modal.style.display = 'none';
  }
}

// Payment integration using Chapa test keys via API route
async function buyProduct(product) {
  const email = prompt('Enter your email:');
  const firstName = prompt('First name:');
  const lastName = prompt('Last name:');
  if (!email || !firstName) return;
  const amounts = { 'BaseLine': 50000, 'FocusLine': 40333, 'CyberLine': 202000 };
  const amount = amounts[product] || 0;
  try {
    const response = await fetch('/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: amount.toString(), email, first_name: firstName, last_name: lastName })
    });
    const data = await response.json();
    if (data && data.data && data.data.checkout_url) {
      window.location.href = data.data.checkout_url;
    } else {
      alert('Payment initialization failed');
    }
  } catch (err) {
    alert('Error: ' + err.message);
  }
}

// FAQ accordion toggling
// Attach a click listener to each FAQ question button. When clicked, the corresponding
// answer div is toggled between hidden and visible states. The button’s aria-expanded
// attribute is updated to reflect the open/closed state. This logic runs once when the
// script is loaded and works across all languages because it targets elements by
// their classes rather than text content.
document.querySelectorAll('.faq-question').forEach(btn => {
  btn.addEventListener('click', () => {
    const answer = btn.nextElementSibling;
    if (answer) {
      const isOpen = answer.classList.toggle('open');
      btn.setAttribute('aria-expanded', isOpen);
    }
  });
});
